import java.util.ArrayList;

public class DnaTagFinderThreeCodons {
  public static void main(String[] args) {
    DnaTagFinderThreeCodons tf = new DnaTagFinderThreeCodons();
    // FileResource fr = new FileResource();
    String dna = "";
    ArrayList<String> genes = tf.findAllGenes(dna);
    System.out.format("Found total %d genes.%n%n", genes.size());
    int count = tf.countCodon(dna, "CTG");
    System.out.println("CTG appears " + count + " times.");
    for (String gene : genes) {
      System.out.println("Gene: " + gene);
      System.out.println("len: " + gene.length());
      System.out.println("CG Ratio: " + tf.getCGRatio(gene));
      System.out.println("____________________________________________");
    }

    int longGenes = 0; // counter for genes > 60
    for (String gene : genes) {
        if (gene.length() > 60) {
            longGenes++;
        }
        System.out.println("Gene: " + gene);
        System.out.println("len: " + gene.length());
        System.out.println("CG Ratio: " + tf.getCGRatio(gene));
        System.out.println("____________________________________________");
    }

    System.out.println("Number of genes longer than 60: " + longGenes);

    int highCGRatioGenes = 0; // counter for genes with CG ratio > 0.35
for (String gene : genes) {
    double cgRatio = tf.getCGRatio(gene);
    if (cgRatio > 0.35) {
        highCGRatioGenes++;
    }
}
System.out.println("Number of genes with CG ratio > 0.35: " + highCGRatioGenes);

int maxLength = 0;
for (String gene : genes) {
    if (gene.length() > maxLength) {
        maxLength = gene.length();
    }
}
System.out.println("Length of the longest gene: " + maxLength);
  }

  public int countCodon(String dna, String codon) {
    dna = dna.toUpperCase();
    codon = codon.toUpperCase();
    int count = 0;
    int index = dna.indexOf(codon);

    while (index != -1) {
      count++;
      // move to the next position after this codon
      index = dna.indexOf(codon, index + 3);
    }

    return count;
  }

  public double getCGRatio(String gene) {
    gene = gene.toLowerCase();
    int count = 0;
    for (char ch : gene.toCharArray()) {
      if (ch == 'c' || ch == 'g') {
        count++;
      }
    }
    return (double) count / gene.length();
  }

  public int findStopCodon(String dnaStr, int startIndex, String codon) {
    int end = dnaStr.indexOf(codon, startIndex + 3);
    while (end != -1 && (end - startIndex) % 3 != 0) {
      end = dnaStr.indexOf(codon, end + 1);
    }
    return end;
  }

  public int[] findGene(String dna) {
    dna = dna.toLowerCase();
    // String gene = "";
    int start = dna.indexOf("atg");
    if (start == -1) {
      return new int[] { -1, -1 };
    }

    int taaEnd = findStopCodon(dna, start, "taa");
    int tagEnd = findStopCodon(dna, start, "tag");
    int tgaEnd = findStopCodon(dna, start, "tga");

    int min = taaEnd;
    for (int i : new int[] { taaEnd, tagEnd, tgaEnd }) {
      if (i == -1) {
        continue;
      }
      if (min == -1) {
        min = i;
      }
      if (i < min) {
        min = i;
      }
    }

    if (min == -1) {
      return new int[] { -1, -1 };
    } else {
      return new int[] { start, min };
    }
  }

  public ArrayList<String> findAllGenes(String dna) {
    dna = dna.toLowerCase();
    ArrayList<String> genes = new ArrayList<String>();
    int[] indices = findGene(dna);
    int start = indices[0];
    int end = indices[1];
    if (start != -1 && end != -1) {
      String gene = dna.substring(start, end + 3);
      genes.add(gene);
      while (start != -1 && end != -1) {
        dna = dna.substring(end + 3);
        start = dna.indexOf("atg");
        if (start == -1) {
          break;
        }
        dna = dna.substring(start);
        indices = findGene(dna);
        start = indices[0];
        end = indices[1];
        if (start != -1 && end != -1) {
          gene = dna.substring(start, end + 3);
          genes.add(gene);
        } else {
          break;
        }
      }
      return genes;
    } else {
      return genes;
    }
  }
}